    const change=(e)=>{
        setQuery(e.target.value)
        console.log(e.target.value)
    }

    const search=async(e)=>{
        if(e.code=="Enter"){
            let data=await fetchweather(query)
            setWhether(data)
            console.log(data)
        }
    }
    return(
        <div>
            <input type="text"  value={query} onClick={change}  onKeyPress={search} placeholder="Enter City Name...!"/>
        </div>
    )
}

export default App;
